fun main(args: Array<String>) {
    val inputFiles = arrayOf("src/main/resources/alias-default.json", "src/main/resources/alias-dark.json")
    val outputFiles = arrayOf("src/main/output/blu-json-light.json", "src/main/output/blu-json-dark.json")

    for (i in inputFiles.indices) {
        JsonUtility().jsonConversion(inputFiles[i], outputFiles[i])
    }
}