#if canImport(SwiftUI)
import SwiftUI
@available(iOS 13.0, macOS 10.15, *)
public extension View {
    func shadow(_ shadow: BluShadow) -> some View {
        self.shadow(
            color: shadow.color,
            radius: shadow.radius,
            x: shadow.position.x,
            y: shadow.position.y
        )
    }
}
@available(iOS 13.0, macOS 10.15, *)
public extension Text {
    func font(_ font: BluTypography) -> Text {
        self.font(font.font)
    }

    func typography(_ typography: BluTypography, color: SwiftUI.Color) -> Text {
        self
        .font(typography)
        .foregroundColor(color)
    }
}
#endif
// TODO: Remove canImport when the proper build command is found. Currently we are using swift build but this is not ideal.
#if canImport(UIKit) // to keep the swift build command from producing compile error
import UIKit
@available(iOS 13.0, macOS 10.15, *)
public extension UIFont {
    static func custom(typography: BluTypography) -> UIFont {
        return UIFont(name: typography.loadableFontName, size: typography.size) ?? .systemFont(ofSize: typography.size)
    }
}
#endif
