#if canImport(SwiftUI)
import SwiftUI
@available(iOS 13.0, macOS 10.15, *)
/// Represents the typography defined by design tokens.
public struct BluTypography {
    public let typeface: Typeface
    public let weight: FontWeight
    public let size: CGFloat
    public let lineHeight: CGFloat
    public let font: Font
    
    public var fontName: String {
        return typeface.rawValue+"-"+weight.rawValue
    }
    
    /// May subject to change in the future
    public var loadableFontName: String {
        return typeface.rawValue+"-"+weight.loadableFontSuffix
    }
}
@available(iOS 13.0, macOS 10.15, *)
public typealias BluFont = BluTypography
#endif

