import Foundation
struct RGBA {
    let r: Double
    let g: Double
    let b: Double
    let a: Double
}

extension RGBA {
    init(r: Double,g: Double,b: Double) {
        self.init(r: r, g: g, b: b, a: 255)
    }
}

extension RGBA {
    public static let clear: Self = .init(r: 0, g: 0, b: 0, a: 0)
}
