const sanitize = require('sanitize-filename')

const sanitizeOptions = {
  replacement: '_'
}

const specialChars = /[@$+^]/g

function sanitizeFileName (name) {
  const fileName = sanitize(name, sanitizeOptions) || ''

  // clean for more special chars
  return fileName.replace(specialChars, sanitizeOptions.replacement)
}

module.exports = {
  sanitizeFileName
}
