const sizes = {
  mobile: {
    width: 360,
    height: 640
  },
  desktop: {
    width: 1280,
    height: 720
  }
}

async function setMobile (page, {
  width = sizes.mobile.width,
  height = sizes.mobile.height
} = {}) {
  await page.setViewportSize({
    width,
    height
  })
}

async function setDesktop (page, {
  width = sizes.desktop.width,
  height = sizes.desktop.height
} = {}) {
  await page.setViewportSize({
    width,
    height
  })
}

module.exports = {
  setMobile,
  setDesktop,
  sizes: JSON.parse(JSON.stringify(sizes))
}
