const fsUtil = require('../../utils/fs')
const configUtil = require('../../utils/config')

const fileName = 'blibli-integration-test.config.js'

function generate (options) {
  const integrationConfigFormat = configUtil.formats[options.integrationType]

  console.log(`Generating a ${options.integrationType} integration test config file...`)

  fsUtil.writeFileIfNotExists(fileName, integrationConfigFormat)
}

module.exports = {
  generate
}
