const { mainUi } = require('../blibli-integration-test.config')
const chalk = require('chalk')
const { logLabel } = require('./utils/log-util')

function _checkRunningProject () {
  console.log(logLabel.WARN, chalk.yellow('Please run your project again by running this following command: '))

  if (mainUi) {
    console.log(logLabel.RUN, 'npm run integration:run-main')
  }
  
  console.log(logLabel.RUN, 'npm run dev-nomock')
}

const execute = () => _checkRunningProject()

module.exports = {
  execute
}