const path = require('path')
const packageJson = require('../../package.json')

function _getProjectRoot () {
  const workDir = process.cwd()
  // follow logic @ https://github.com/inxilpro/node-app-root-path
  return workDir.split('/node_modules')[0]
}

const appRoot = _getProjectRoot()
const modulePath = path.join(appRoot, 'node_modules', packageJson.name)

module.exports = {
  root: {
    path: appRoot
  },
  module: {
    path: modulePath
  },
  getPath (...args) {
    return path.join(appRoot, ...args)
  },
  getModulePath (...args) {
    return path.join(modulePath, ...args)
  },
  getExtensionFile (file) {
    return path.extname(file)
  }
}
