const chalk = require('chalk')
const { mainUi, app } = require('../helpers/blibli-integration-test.config')

const mainUiPort = mainUi?.port
const appPort = app?.port

const ports = [mainUiPort, appPort]

const listenPort = ports.filter((el) => el !== undefined).join(' and ')

const portStatus = mainUiPort || appPort

function printListeningPorts () {
  if (!portStatus) {
    return
  }

  console.log(chalk.bgBlue(
    `Your app currently running in port ${listenPort}...`
  ))
}

module.exports = {
  printListeningPorts
}
